insert into subthemes(theme_id, subtheme_name, subtheme_file_url) values (4, 'Наставляемый впервые поступил на гражданскую государственную службу', 'static/downloads/Индивидуальный_план_мероприятий_по_наставничеству_для_впервые_поступившего.docx'), (4, 'Наставляемый ранее уже замещал должность на гражданской государственной службе', 'static/downloads/Индивидуальный_план_мероприятий_по_наставничеству_для_ранее_уже.docx');